import { flushPromises, mount } from '@vue/test-utils';
import FileField from './FileField.vue';
import { describe } from 'vitest';

describe('Test for FileField component', () => {
  test('FileField component', async () => {
    expect(FileField).toBeTruthy();
    const initialValue = new File([], 'test');

    const wrapper = mount(FileField, {
      props: {
        name: 'test',
        modelValue: initialValue,
      },
    });

    await flushPromises();

    const span = wrapper.get('[data-testid="file-preview"]');
    console.log(span);
    expect((span.element as HTMLSpanElement).innerHTML).toEqual('test');
  });
});
