<template>
  <button :disabled="disabled" :class="['invert image-select__item', { active }]" @click="clickHandler">
    <img :src="item.image" :alt="item.label" class="image-select__item-image" />
  </button>
</template>

<script lang="ts" setup>
import type { ImageOption } from '../../../type/form/ImageOption';

interface Props {
  item: ImageOption;
  active?: boolean;
  disabled?: boolean;
}

const props = defineProps<Props>();
const emit = defineEmits(['click']);

const clickHandler = (e: MouseEvent) => {
  e.preventDefault();
  e.stopPropagation();

  emit('click', props.item);
};
</script>
