<template>
  <FieldWrapper
    :label="label"
    :error="error"
    :info="info"
    :class-name="`field textarea-field ${className || ''} ${focused ? 'focus' : ''} ${disabled ? 'disabled' : ''}`"
  >
    <textarea
      type="text"
      :disabled="disabled"
      class="textarea-field__input form-field__input"
      :value="modelValue"
      :placeholder="placeholder"
      :rows="rows"
      @focus="focusHandler"
      @blur="blurHandler"
      @change="changeHandler"
    />
  </FieldWrapper>
</template>

<script lang="ts" setup>
import FieldWrapper from '../FieldWrapper/FieldWrapper.vue';
import { ref } from 'vue';

interface Props {
  label?: string;
  error?: string;
  disabled?: boolean;
  className?: string;
  name: string;
  modelValue?: string;
  placeholder?: string;
  info?: string;
  rows?: number;
}

const props = defineProps<Props>();
const emit = defineEmits(['update:modelValue']);
const focused = ref<boolean>(false);

const focusHandler = () => {
  focused.value = true;
};

const blurHandler = () => {
  focused.value = false;
};

const changeHandler = (event: Event) => {
  emit('update:modelValue', (event.target as HTMLInputElement).value, props.name);
};
</script>
