<template>
  <label :ref="setRef" :class="['toggle', { disabled }]">
    <span v-if="label?.length" class="toggle__label">
      {{ label }}
    </span>
    <input
      type="checkbox"
      :class="`${disabled ? 'disabled' : ''}`"
      class="toggle__input"
      :disabled="disabled"
      :checked="modelValue"
      @change="changeHandler"
    />
    <span class="toggle__wrapper">
      <span class="toggle__handle" />
    </span>
  </label>
</template>

<script lang="ts" setup>
import { ref } from 'vue';

const props = defineProps<{
  label?: string;
  error?: string;
  name: string;
  modelValue?: boolean;
  disabled?: boolean;
}>();
const emit = defineEmits(['update:modelValue']);
const wrapperRef = ref<unknown>(null);
defineExpose({ wrapperRef });

const setRef = (val: unknown) => {
  wrapperRef.value = val;
};

const changeHandler = (event: Event) => {
  emit('update:modelValue', (event.target as HTMLInputElement).checked, props.name);
};
</script>
