import { flushPromises, mount } from '@vue/test-utils';
import Flex from './Flex.vue';
import { describe } from 'vitest';

describe('Test for Flex component', () => {
  test('Flex component', async () => {
    expect(Flex).toBeTruthy();

    const wrapper = mount(Flex, {
      props: {
        direction: 'row',
        alignItems: 'stretch',
        justifyContent: 'stretch',
        mDirection: 'row',
        mAlignItems: 'stretch',
        mJustifyContent: 'stretch',
      },
    });

    await flushPromises();
    expect(wrapper.element.classList).toContain('flex-layout');
  });
});
