<template>
  <div :class="['flex-layout', direction, className]">
    <slot />
  </div>
</template>

<script lang="ts" setup>
interface Props {
  className?: string;
  direction: 'row' | 'row-reverse' | 'column' | 'column-reverse';
  alignItems: 'stretch' | 'center' | 'flex-start' | 'flex-end' | 'baseline';
  justifyContent: 'stretch' | 'center' | 'flex-start' | 'flex-end' | 'baseline';

  mDirection: 'row' | 'row-reverse' | 'column' | 'column-reverse'; // for mobile
  mAlignItems: 'stretch' | 'center' | 'flex-start' | 'flex-end' | 'baseline'; // for mobile
  mJustifyContent: 'stretch' | 'center' | 'flex-start' | 'flex-end' | 'baseline'; // for mobile
}

withDefaults(defineProps<Props>(), {
  className: '',
  direction: 'row',
  alignItems: 'center',
  justifyContent: 'stretch',
  mAlignItems: (props) => props.alignItems,
  mDirection: (props) => props.direction,
  mJustifyContent: (props) => props.justifyContent,
});
</script>

