@use '../../../sass/variables/box-model';

.flex-layout {
  display: flex;
  gap: box-model.$spacing-main;
  flex-direction: v-bind('mDirection');
  justify-content: v-bind('mJustifyContent');
  align-items: v-bind('mAlignItems');

  @media (min-width: 991px) {
    flex-direction: v-bind('direction');
