<template>
  <div class="tab-nav">
    <div class="container">
      <div class="tab-nav__items">
        <a
          v-for="item in items"
          :key="item.key"
          href="#"
          :class="['tab-nav__item subheading', { active: item.isActive || item.key === active }]"
          @click="tabClickHandler(item.key)"
        >
          <span>
            {{ item.title }}
          </span>
        </a>
      </div>
      <div class="tab-nav__buttons">
        <slot />
      </div>
    </div>
  </div>
</template>

<script lang="ts" setup>
import type { Nav } from '../../../type/etc/nav';

interface Props {
  items: Nav[];
  active?: string;
}

defineProps<Props>();
const emit = defineEmits(['change']);

const tabClickHandler = (newTab: string) => emit('change', newTab);
</script>
