<template>
  <div :class="['alert', type, { show: isShow }]">
    <div class="container">
      <div class="alert__content" v-html="content" />
      <button type="button" class="invert alert__btn-close" @click="closeClickHandler">
        <slot name="close">
          <Icon name="close" />
        </slot>
      </button>
    </div>
  </div>
</template>

<script lang="ts" setup>
import type { Alert } from '../../../type/notification/alert';
import Icon from '../Icon/Icon.vue';
import { onMounted, ref, onUnmounted } from 'vue';

interface Props extends Alert {}
const props = defineProps<Props>();
const emit = defineEmits(['close']);
const isShow = ref<boolean>(false);
let timerDescriptor = 0;

const closeClickHandler = () => {
  isShow.value = false;
  setTimeout(() => {
    emit('close', props);
  }, 400);
};

onMounted(() => {
  setTimeout(() => {
    isShow.value = true;
  }, 10);

  timerDescriptor = window.setTimeout(() => {
    closeClickHandler();
  }, props.ttl);
});

onUnmounted(() => {
  if (timerDescriptor) {
    clearTimeout(timerDescriptor);
  }
});
</script>

<style lang="scss">
@import './Alert';
</style>
