<template>
  <div class="avatar">
    {{ abbr }}
  </div>
</template>

<script lang="ts" setup>
import { computed } from 'vue';

interface Props {
  name: string;
}

const props = defineProps<Props>();

const abbr = computed(() => {
  const parts = props.name.split(' ');

  return `${parts?.[0]?.[0] || ''}${parts?.[1]?.[0] || parts?.[0]?.[1] || ''}`;
});
</script>
