<template>
  <div :class="['collapse', { show: isShow }]">
    <div class="collapse__content">
      <slot />
    </div>
    <div class="collapse__footer">
      <button class="invert" type="button" @click="toggleClickHandler">
        <slot name="toggle" :is-show="isShow">
          {{ isShow ? trans.trans('Close') : trans.trans('Show') }}
        </slot>
      </button>
    </div>
  </div>
</template>

<script lang="ts" setup>
import { ref } from 'vue';
import useTranslator from '../../../providers/translator';

interface Props {
  rows?: number;
}

withDefaults(defineProps<Props>(), {
  rows: 3,
});
const trans = useTranslator();

const isShow = ref<boolean>(false);

const toggleClickHandler = () => {
  isShow.value = !isShow.value;
};
</script>

<style lang="scss" scoped>
@import 'Collapse';

.collapse__content {
  -webkit-line-clamp: v-bind('rows');
}
</style>
