<template>
  <span :class="['svg-image', name, className, iconFontConfig.font, `${iconFontConfig.prefix}${name}`]" />
</template>

<script setup lang="ts">
import { useIcons } from '../../../providers/icons';

interface IconProps {
  name: string;
  className?: string;
}

const props = defineProps<IconProps>();

const iconConfigProvider = useIcons();

const iconFontConfig = iconConfigProvider.iconConfig;

const name = iconFontConfig.iconMap[props.name] ?? props.name;
</script>
