<template>
  <button v-if="totalPages > 1 || page < totalPages" type="button" @click.prevent="btnClickHandler">
    {{ text || provider.trans('Load more') }}
  </button>
</template>

<script lang="ts" setup>
import { ref } from 'vue';
import useTranslator from '@/providers/translator';

interface Props {
  currentPage: number;
  totalPages: number;
  text?: string;
}
const provider = useTranslator();

const props = defineProps<Props>();
const page = ref<number>(props.currentPage);
const emit = defineEmits(['change']);

const btnClickHandler = () => {
  page.value += 1;
  emit('change', page.value);
};
</script>
