<template>
  <Modal
    :model-value="modelValue"
    class-name="modal-confirmator"
    :title="title"
    :is-loading="isLoading"
    :accept="accept"
    :accept-icon="acceptIcon"
    :cancel="cancel"
    :cancel-icon="cancelIcon"
    @update:model-value="changeModelValueHandler"
    @accept="acceptClickHandler"
  >
    <slot />
  </Modal>
</template>

<script lang="ts" setup>
import Modal from './Modal.vue';

interface Props {
  title?: string;
  accept?: string;
  acceptIcon?: string;
  cancel?: string;
  cancelIcon?: string;
  modelValue: boolean;
  context?: unknown;
  isLoading?: boolean;
}
const props = defineProps<Props>();

const emit = defineEmits(['update:modelValue', 'accept']);

const changeModelValueHandler = (newValue: boolean) => emit('update:modelValue', newValue);

const acceptClickHandler = () => emit('accept', props.context);
</script>
