<template>
  <div class="progress-pipeline">
    <div class="container">
      <ProgressItem v-for="item in items" :key="item.key" :item="item" @click="clickHandler" />
    </div>
  </div>
</template>

<script lang="ts" setup>
import type { Nav } from '../../../../type/etc/nav';
import ProgressItem from './ProgressItem.vue';

interface Props {
  items: Nav[];
  withStart?: boolean;
  withEnd?: boolean;
}

defineProps<Props>();
const emit = defineEmits(['click']);
const clickHandler = (item: Nav) => emit('click', item);
