<template>
  <div :class="['skeleton', variant]" :style="style" />
</template>

<script lang="ts" setup>
import { computed } from 'vue';

interface Props {
  w?: number;
  variant?: 'default' | 'animated';
}

const props = withDefaults(defineProps<Props>(), {
  w: 10,
  variant: 'default',
});

const style = computed(() => {
  return {
    width: props.w <= 0 ? '10%' : props.w >= 10 ? '100%' : `${props.w * 10}%`,
  };
});
</script>

<style lang="scss">
@import 'Skeleton';
</style>
