<template>
  <table class="table-view">
    <thead>
      <tr>
        <TableHead v-for="item in header" :key="item.key" :item="item" @sort="headSortHandler" />
      </tr>
    </thead>
    <tbody>
      <template v-if="data.length">
        <tr v-for="item in data" :key="keygen(item)" class="table-view__row">
          <slot name="row" :row="item">
            <td v-for="col in header" :key="col.key" :class="['table-view__cell', col.key]">
              <slot :name="`cell-${col.key}`" :cell="accessor(item, col.key)" :row="item">
                {{ accessor(item, col.key) }}
              </slot>
            </td>
          </slot>
        </tr>
      </template>
      <tr v-else class="table-view__row table-view__skel-tr">
        <td v-for="th in header" :key="`skel-${th.key}`" :class="['table-view__skel-td', th.key]">
          <button v-if="th.key === 'actions'" class="invert" disabled>
            <Icon :name="emptyActionsIcon || 'dots-horizontal'" />
          </button>
          <span v-else class="table-view__skel" />
        </td>
      </tr>
    </tbody>
  </table>
</template>

<script lang="ts" setup>
import type { TheadProps } from '../../../type/table/Thead';
import TableHead from './TableHead.vue';
import type { SortingState } from '../../../type/etc/SortingState';
import Icon from '../Icon/Icon.vue';

interface TableProps {
  header: TheadProps[];
  data: unknown[];
  emptyActionsIcon?: string;
  keygen: (item: unknown) => string;
}

defineProps<TableProps>();
const emit = defineEmits(['sort']);

const accessor = (item: unknown, key: string) => {
  return (item as Record<string, unknown>)[key];
};

const headSortHandler = (params: SortingState) => emit('sort', params);
</script>
