<template>
  <div :class="['toast', type, { show: isShow }]">
    <div class="toast__content">
      <slot name="content" />
    </div>
    <button type="button" class="invert toast__btn-close" @click="closeClickHandler">
      <slot name="close">
        <Icon name="close" />
      </slot>
    </button>
  </div>
</template>

<script lang="ts" setup>
import type { Toast } from './type/toast';
import Icon from '../Icon/Icon.vue';
import { onMounted, ref, onUnmounted } from 'vue';

interface Props extends Toast {}
const props = defineProps<Props>();
const emit = defineEmits(['close']);
const isShow = ref<boolean>(false);
let timerDescriptor = 0;

const closeClickHandler = () => {
  isShow.value = false;
  setTimeout(() => {
    emit('close', props);
  }, 400);
};

onMounted(() => {
  setTimeout(() => {
    isShow.value = true;
  }, 10);

  timerDescriptor = window.setTimeout(() => {
    closeClickHandler();
  }, props.ttl);
});

onUnmounted(() => {
  if (timerDescriptor) {
    clearTimeout(timerDescriptor);
  }
});
</script>

<style lang="scss">
@import './Toast';
</style>
