export function shortFormat(num: number): string {
  let div = 1;
  let suffix = '';

  if (num > 999999) {
    div = 1000000;
    suffix = 'M';
  } else if (num > 999) {
    div = 1000;
    suffix = 'K';
  }

  return `${Math.floor(num / div)} ${suffix}`;
}

export const formatNumber = new Intl.NumberFormat('en-GB').format;
