export function ensureTrailingSlash(value: string): string {
  return value + (value.endsWith('/') ? '' : '/');
}

export function isCurrentPathStartsWith(relativePath: string): boolean {
  return ensureTrailingSlash(window.location.pathname).startsWith(ensureTrailingSlash(relativePath.split('?')[0]));
}

export function getBasePath(url: string): string {
  return url.split('?')[0];
}
