import { describe } from 'vitest';
import { formatISODate, isExpiredDate } from '../../src/utils/date';

describe('Test for date utils', () => {
  const DATE_IN_PAST = '2000-01-01T09:00:00.000Z';
  const DATE_IN_FUTURE = '2999-12-31T09:00:00.000Z';

  test('formatISODate', () => {
    expect(formatISODate(DATE_IN_PAST, 'yyyy')).toEqual('2000');
  });

  test('isExpiredDate', () => {
    expect(isExpiredDate(DATE_IN_FUTURE)).toBeFalsy();
    expect(isExpiredDate(DATE_IN_PAST)).toBeTruthy();
  });
});
